import { useState } from "react";
import Logo from "./Logo";

export default function App() {
  const [items, setItems] = useState([]);

  function handleAddItems(item) {
    setItems((items) => [...items, item]);
  }

  function handleDeleteItem(id) {
    setItems((items) =>items.filter((item) => item.id !== id));
  }

  function handleToggleItem(id) {
    setItems(items => 
      items.map(item => item.id === id ? {...item, packed: !item.packed} 
      : item
    )
  );
}

function handleClearList() {
  const confirmed = window.confirm(
    "Are you sure you want to delete all items?"
  );

  if(confirmed) setItems([]);
}

return (
  <div className="app">
    <Logo />
    <Form onAddItems={handleAddItems} />
    <PackingList 
      items={items} 
      onDeleteItem={handleDeleteItem} 
      onToggleItem={handleToggleItem}
      onClearList={handleClearList}
    />
      <Stats items={items} />
    </div>
  );
}


function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);


  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return;

    const newItem = { description, quantity, packed: false, id: Date.now() };

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button>Add</button>
    </form>
  );
}

function PackingList({ items, onDeleteItem, onToggleItem, onClearList }) {
  const [sortBy, setSortBy] = useState("input");

  let sortedItems;

  if (sortBy === "input") sortedItems = items;

  if (sortBy === "description") 
  sortedItems = items
  .slice()
  .sort((a, b) => a.description.localCompare(b.description));

  if (sortBy === "packed")
  sortedItems = items
  .slice()
  .sort((a, b) => Number(a.packed) - Number(b.packed));

  return (
    <div className="list">
      <ul>
        {sortedItems.map((item) => (
          <Item 
          item={item} 
          onDeleteItem={onDeleteItem} 
          onToggleItem={onToggleItem}
          key={item.id} 
          />
        ))}
      </ul>

      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort by input order</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed status</option>
        </select>
        <button onClick={onClearList}>Clear list</button>
      </div>
    </div>
  );
}

function Item({ item, onDeleteItem, onToggleItem }) {
  return (
    <li>
      <input 
      type="checkbox" 
      value={item.packed} 
      onChange={() => onToggleItem(item.id)} 
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={()=> onDeleteItem(item.id)}>‚ùå</button>
    </li>
  );
}

function Stats({ items }) {
  if (!items.lenght)
  return(
    <p className="stats">
      <em>
        Start adding some items to your packing list üöÄ
      </em>
      </p>
  );

  const numItems = items.lenght;
  const numPacked = items.filter((item) =>item.packed).lenght;
  const percentage = Math.round((numPacked / numItems) * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100? 'You got everything! Ready to go ‚úàÔ∏è' 
        : `üíº You have ${numItems} items on your list, and you already packed ${numPacked} (${percentage}%)`}
        </em>
    </footer>
  );
}
